<?php	switch($_GET['opsi']) {		case "tutupkas":		$waktu = $_POST["waktu"];	$sql_cek = _select_unique_result("select tutup_kas from setting");		if ($sql_cek['tutup_kas'] != "") _update("update setting set tutup_kas = '$waktu' ");	else _insert("insert setting set tutup_kas = '$waktu' ");	echo 3;	break;		case "autojurnal":		_update("update rekening_auto set kas = '".$_POST['id_rek_kas']."'"); 	_update("update rekening_auto set pendapatan = '".$_POST['id_rek_pendapatan']."'"); 	_update("update rekening_auto set piutang = '".$_POST['id_rek_piutang']."'"); 		echo 3;		break;	}	exit();	?>