<script type="text/javascript">	function load(){		if($("#klinik").val()=="" || $("#waktu").val()=="" || $("#id_klinik").val()==""){				caution('Maaf, form input pencarian belum terisi dengan benar','#alert');		}else{			contentloader('<?php echo app_base_url('/rekam-medik/informasi/bor-avlos-toi-bto?ruang=')?>'+$("#klinik").val()+"&waktu="+$("#waktu").val()+"&id_klinik="+$("#id_klinik").val(),"#content");		}	}    $(function() {		$("#reset").click(function(){			$("#klinik").val("") ;			$("#waktu").val("") ;			$("#id_klinik").val("");		});       $( "#waktu" ).datepicker({        changeMonth: true,        changeYear: true,        showButtonPanel: true,        dateFormat: 'yy-mm',        onClose: function(dateText, inst) {             var month = $("#ui-datepicker-div .ui-datepicker-month :selected").val();            var year = $("#ui-datepicker-div .ui-datepicker-year :selected").val();            $(this).datepicker('setDate', new Date(year, month, 1));			$("#search").focus();        },        beforeShow : function(input, inst) {            if ((datestr = $(this).val()).length > 0) {                actDate = datestr.split('-');                year = actDate[0];                month = actDate[1]-1;                $(this).datepicker('option', 'defaultDate', new Date(year, month));                $(this).datepicker('setDate', new Date(year, month));            }        }    });	$('#klinik').autocomplete("<?= app_base_url('/rekam-medik/search?opsi=instalasi_inap') ?>",        {			            parse: function(data){                var parsed = [];                for (var i=0; i < data.length; i++) {                    parsed[i] = {                        data: data[i],                        value: data[i].nama // nama field yang dicari                    };                }                return parsed;            },            formatItem: function(data,i,max){                var str='<div class=result>'+data.nama+' <br/></div>';                return str;            },            width: 200, // panjang tampilan pencarian autocomplete yang akan muncul di bawah textbox pencarian            dataType: 'json' // tipe data yang diterima oleh library ini disetup sebagai JSON        }).result(        function(event,data,formated){            $(this).attr('value',data.nama);            $('#id_klinik').attr('value',data.id);        }    );		    });</script> <style>    .ui-datepicker-calendar {        display: none;        }	.ui-datepicker-current {        display: none;        }    </style><div class="data-input"><h1>Informasi BOR-AVLOS-TOI-BTO</h1><fieldset><legend>Parameter Pencarian</legend>        <form action="" method="get" >            <label for="norm">Klinik Inap</label><input type="text" name="klinik" id="klinik" />			<input type="hidden" name="id_klinik" id="id_klinik" />            <label for="norm">Bulan dan Tahun</label><input type="text" name="waktu" id="waktu" readonly='readonly'/>            <fieldset class="input-process">                <input type="button" value="Cari" id="search" class="tombol" onClick="load()"/>                <input type="button" value="Batal" class="tombol" id="reset"/>            </fieldset>        </form>   </fieldset></div><div id="alert"></div><div id="content"></div>