<center><strong><h1>Indikator Pasien Rawat Inap Ruang <?php echo $_GET['ruang'] ." Periode ". $_GET['waktu']?> </h1></strong></center><?phpif(isset($_GET['waktu']) &&  isset($_GET['id_klinik'])){require_once "app/lib/common/master-data.php";$record_data = borMuatData($_GET['id_klinik'],$_GET['waktu']);?><div class='data-list'><fieldset><legend>Informasi Frekuensi Pasien Rawat Instalasi <?php echo $_GET['ruang'] ;?></legend><div class='floleft' style="padding:0 50px 0 0"><table class='data-list tabel' ><tr><th>Tanggal</th><th>Pasien Awal</th><th>Pasien Masuk</th><th>Pasien Keluar</th><th>Lama Dirawat</th><th>Hari Perawatan</th></tr><?php$urutan_tanggal = null;$awal = $record_data['jml_masuk_bulan_lalu'];$tot_awal=0;$tot_masuk=0;$tot_keluar=0;$tot_akhir=0;$akhir = 0;$counter=0;foreach($record_data['bulan_ini'] as $data){	$akhir = $awal + $data['jml_masuk'] - $data['jml_keluar'];	$selisih = date("d",strtotime($data['tanggal'])-strtotime($urutan_tanggal))-1;	if($urutan_tanggal!=null && ($selisih) > 1){		for($i=2;$i<=$selisih;$i++){			$urutan_tanggal = date("Y-m-d",strtotime("+1 day",strtotime($urutan_tanggal)));			$class = ($counter%2)?"odd":"even";			echo "<tr align='center' class='$class'><td>$urutan_tanggal</td><td>$awal</td><td>0</td><td>0</td><td>--</td><td>$awal</td></tr>";			$counter++;		}	}	$class = ($counter%2)?"odd":"even";	echo "<tr align='center' class='$class'><td>$data[tanggal]</td><td>$awal</td><td>$data[jml_masuk]</td><td>$data[jml_keluar]</td><td>--</td><td>$akhir</td></tr>";	$tot_awal+=$awal;	$tot_masuk+=$data['jml_masuk'];	$tot_keluar+=$data['jml_keluar'];	$tot_akhir+=$akhir;	$awal = $akhir;	$urutan_tanggal = $data['tanggal'];	$counter++;}echo "<tr align='center'><td>jumlah</td><td>$tot_awal</td><td>$tot_masuk</td><td>$tot_keluar</td><td>--</td><td>$tot_akhir</td></tr>";echo "</table></div>";?><div ><h1>Data Statistik 		:</h1></br><table><tr><td>1. BOR</td><td>: <?php echo ($tot_akhir==0 || $record_data['jml_bed']['jml_bed']==0)?"<i>Undefined</i>":($tot_akhir / $record_data['jml_bed']['jml_bed'] / $record_data['hari']) * 100;?></td></tr><tr><td>2. AVLOS</td><td>: <?php echo ($tot_keluar==0 || $tot_akhir==0)?"<i>Undefined</i>":$tot_akhir / $tot_keluar ; ?></td></tr><tr><td>3. TOI</td><td>: <?php echo ($tot_keluar==0 || $record_data['jml_bed']['jml_bed']==0)?"<i>Undefined</i>":($record_data['jml_bed']['jml_bed'] * $record_data['hari']) / $tot_keluar;?></td></tr><tr><td>4. BTO</td><td>: <?php echo ($tot_keluar==0 || $record_data['jml_bed']['jml_bed']==0)?"<i>Undefined</i>":$tot_keluar / $record_data['jml_bed']['jml_bed']  ;?></td></tr></table></div></fieldset></div><?php}exit();